apartmentName, dealAmount, area, date, dong, lat, lng
from home.apt_table 
where dongCode=#{fullCode}
limit #{pageNum}, #{pageSize};

select 
apartmentName, dealAmount, area, date, dong, lat, lng
from home.apt_table 
where dong=#{dongName}
limit #{pageNum}, #{pageSize};

select 
apartmentName, dealAmount, area, date, dong, lng, lat
from apt_table 
where lng = #{lng} and lat = #{lat}
limit #{pageNum}, #{pageSize};
