mapper.select:
select dongCode, dong, lng, lat 
from houseinfo 
where dongCode = #{code}

select *
from apt_table
where lat = #{lat} and lng = #{lng}

select 
apartmentName, dealAmount, area, date, dong, lat, lng
from home.apt_table 
where dongCode=#{fullCode}
limit #{pageNum}, #{pageSize};

select 
apartmentName, dealAmount, area, date, dong, lat, lng
from home.apt_table 
where dong=#{dongName}
limit #{pageNum}, #{pageSize};

select 
apartmentName, dealAmount, area, date, dong, lng, lat
from apt_table 
where apartmentName = #{aptName}
limit #{pageNum}, #{pageSize};

select lc
from apt_table
where apt_no = #{no}

mapper.update:
update apt_table
set lc = lc + 1
where apt_no = #{no}

mapper.select:
select t.apt_no, dongCode, apartmentName, dealAmount, area, date, lng, lat, dong, lc
from apt_like l inner join apt_table t on l.apt_no = t.apt_no
where user_id = #{id}
