mapper.select:
select user_id, password, name, age, email, domain, grade, join_date
from member
where user_id = #{id}

mapper.update:
update member
set password = #{password}, email = #{email}, domain = #{domain}
where user_id = #{id}

update member
set token = #{token}
where user_id = #{id}

update member
set token = #{token, jdbcType=VARCHAR}
where user_id = #{id}

mapper.select:
select token
from member
where user_id = #{id}

select m.user_id, password, name, age, email, domain, grade, join_date, s_folder, o_file, s_file
from member m inner join profile_info p on m.user_id = p.user_id 
where m.user_id = #{id}
